mapper.delete:
DELETE FROM ACCOUNTS
 WHERE AC_EMAIL = #{ac_email}
   AND AC_PW = #{ac_pw}

mapper.update:
UPDATE ACCOUNTS
   SET AC_PW = #{ac_pw}
 WHERE AC_EMAIL = #{ac_email}

mapper.select:
SELECT AC_IP
  FROM ACCOUNTS
 WHERE AC_EMAIL = #{id}

SELECT AC_NAME
  FROM ACCOUNTS
 WHERE AC_EMAIL = #{sellerEmail}

mapper.update:
UPDATE ACCOUNTS 
SET AC_POINT = AC_POINT - #{use_point} + #{earn_point}
WHERE AC_NAME = #{ac_name}
