mapper.update:
UPDATE FINDSTU
   SET FINDSTU_VIEW = #{findstu_view} + 1
 WHERE FINDSTU_ID = #{findstu_id}

UPDATE FINDSTU_STATICS
   SET FINDSTU_LATEST_VIEW_DATE = SYSDATE
 WHERE FINDSTU_ID = #{findstu_id}

UPDATE FINDSTU
   SET FINDSTU_TITLE = #{findstu_title}
     , FINDSTU_CONTENT = #{findstu_content}
 WHERE FINDSTU_ID = #{findstu_id}
