mapper.select:

select * from (
select rownum rnum, c.* from(

	select * from notice
	order by notice_no desc

	) c
) notice 
where rnum BETWEEN #{startNo} AND #{endNo}


select * from (
select rownum rnum, c.* from(

	select * from notice
	where notice_title like '%' || #{keyword} || '%'
	order by notice_no desc

	) c
) notice 
where rnum BETWEEN #{paging.startNo} AND #{paging.endNo}
