mapper.update:
UPDATE QUESTION
   SET QUESTION_VIEW = #{question_view} + 1
 WHERE QUESTION_ID = #{question_id}

UPDATE QUESTION_STATICS
   SET QUESTION_LATEST_VIEW_DATE = SYSDATE
 WHERE QUESTION_ID = #{question_id}

UPDATE QUESTION
   SET QUESTION_TITLE = #{question_title}
     , QUESTION_CONTENT = #{question_content}
 WHERE QUESTION_ID = #{question_id}
