mapper.select:
SELECT *
FROM CHATROOM AS c
JOIN PRODUCT as p
ON c.pr_id = p.pr_id
WHERE c.sellerid = #{email}
OR c.buyerid = #{email};

mapper.update:
UPDATE CHATROOM
SET FILENAME = #{fileName}
WHERE ID = #{id};

UPDATE CHATROOM
SET CHATREADBUY = #{chatReadBuy}
WHERE ID = #{id};

UPDATE CHATROOM
SET CHATREADSELL = #{chatReadSell}
WHERE ID = #{id};
