mapper.update:
UPDATE SEL_ITEM SET SEL_FIELD = #{sel_field} , SEL_TITLE = #{sel_title} ,
				    SEL_LOCATION = #{sel_location} , SEL_CONTENT = #{sel_content} ,
				    SEL_PRICE = #{sel_price}  WHERE SEL_ID = #{sel_id}

mapper.select:
SELECT SEL_REVIEWCOUNT
  FROM SEL_ITEM
 WHERE SEL_ID = #{itemid}

SELECT SEL_STARSCORE
  FROM SEL_ITEM
 WHERE SEL_ID = #{parseInt}

mapper.update:
UPDATE SEL_ITEM
   SET SEL_REVIEWCOUNT = SEL_REVIEWCOUNT + 1
 WHERE SEL_ID = #{i}

UPDATE SEL_ITEM
   SET SEL_STARSCORE = #{star}
 WHERE SEL_ID = #{sel_id}
